mapper.update:
update activity
set participants_nums = #{participantsNum}
where id=#{id}

mapper.select:
select participants_nums
from activity
where id=#{id}

select 
from activity

  originator_id = #{originatorId}

    and event_name = #{eventName}


    and originator = #{originator}


    and status = #{status}


    and start_Time > #{startTime}


    and end_Time < #{endTime}
